function FeedbackStats({ feedback }) {
  const avgRating = (feedbackArr) => {
    let average = 0;
    feedbackArr.forEach(item => average + item.rating);
    let output = average / feedbackArr.length;
    return output;
  }

  return (
    <div className='feedback-stats'>
      <h4>{feedback.length} Reviews:</h4>
      <h4>Average Rating:{avgRating(feedbackArr)}</h4>
    </div>
  )
}

export default FeedbackStats
