function App() {
  let title = 'Blog Post 1';
  let body = 'this is my blog post';

  let comments = [
    { id: 1, text: 'comment1' },
    { id: 2, text: 'comment2' },
    { id: 3, text: 'comment3' }
  ];

  const loading = false;
  const showComments = false;

  const commentBlock = (
    <div className="comments">
      <h3>Comments ({comments.length})</h3>
      <ul>{comments.map((comment, index) => <li key={index}>{comment.text}</li>)}</ul>
    </div>
  )

  if (loading) {
    return <h1>Loading...</h1>
  }

  return (
    <div className='container'>
      <h1>{title}</h1>
      <p>{body}</p>

      {showComments &&
        commentBlock}


    </div>
  )
}

export default App;
